make_age10 <- function(age,
                       validate_ages = TRUE) {

  # First, validate ages IF ASKED FOR
  if (validate_ages) {
    age <- if_else(age > 100, 100, age)
    age <- if_else(age < 0, 0, age)
  }

  # Then round ages to the nearest 10:
  round(age, digits = -1)
}
make_age10(office_ages)## [1]   0  10  20  40  60  70 100
make_age10(office_ages, validate_ages = FALSE)## [1] -10  10  20  40  60  70 200
make_age10 <- function(age,
                       validate_ages = TRUE,
                       remove_implausible = FALSE) {

  # First, validate ages
  if (validate_ages) {
    if (remove_implausible) {
      # Replace implausible ages with NAs
      age <- if_else(age > 100 | age < 0, NA_real_, age)
    }
    if (!remove_implausible) {
      # Replace implausible ages with their nearest plausible age
      age <- if_else(age > 100, 100, age)
      age <- if_else(age < 0, 0, age)
    }
  }

  # Then round ages to the nearest 10:
  round(age, digits = -1)
}
make_age10(office_ages, validate_ages = TRUE)## [1]   0  10  20  40  60  70 100
make_age10(office_ages, validate_ages = TRUE, remove_implausible = TRUE)## [1] NA 10 20 40 60 70 NA
make_age10 <- function(age,
                       validate_ages = "remove") {

  # First, validate ages
  if (validate_ages == "remove") {
    age <- if_else(age > 100 | age < 0, NA_real_, age)
  }

  if (validate_ages == "adjust") {
    # Replace implausible ages with their nearest plausible age
    age <- if_else(age > 100, 100, age)
    age <- if_else(age < 0, 0, age)
  }

  # Then round ages to the nearest 10:
  round(age, digits = -1)
}
make_age10(office_ages, validate_ages = "adjust")## [1]   0  10  20  40  60  70 100
make_age10(office_ages, validate_ages = "remove")## [1] NA 10 20 40 60 70 NA
